import {Bar} from 'react-chartjs-2'


const Chart=({data:{confirmed,recovered,deaths}})=>{
    return (
        confirmed ?(
        <Bar
            data= {{
                labels: ['iInfected', 'Recovered', 'Deaths'],
                datasets: [{
                    label: 'Peoples',
                    data: [confirmed.value,recovered.value,deaths.value],
                    backgroundColor: [
                        'rgba(0, 0, 255, 0.2)',
                        'rgba(0, 255, 0, 0.5)',
                        'rgba(255, 0,0, 0.5)'
                    ]
                }]
            }}
            options={{
                scales: {
                   legend:{
                       display:false
                   },
                   title:{
                       display:true,
                       text:'current state of the country'
                   }
                }
            }}
        />):''
    )
}

export default Chart;
